import React, { useEffect } from 'react'
import './hero.css'
import ilustra from '../../assets/images/ilustra_header.svg'
import buscar from '../../assets/images/icon-search-mod-noc.svg'

export default function Hero(props) {
    useEffect(() => {
        let autocomplete = fetch(`${props.urlAutocomplete}?api_key=${props.API_KEY}&q=${props.search}`);
        autocomplete.then((res) => { return res.json() })
            .then((dato) => {
                console.log("Autocomplete", dato.data)
            })
    }, [props.search]);

    return (
        <div className="hero">
            <h1>Â¡Inspirate y busca los mejores <span>GIFS!</span></h1>
            <img src={ilustra} alt="Imagen del hero" />
            <div>
                <input type="text" onChange={props.handleSearchChange} value={props.search} placeholder="Busca gifs" />
                <button type="submit" className="btn-submit" onClick={props.handleSubmit}><img src={buscar} alt="Icono lupa" /></button>
            </div>
        </div>
    )
}
