import React, { useState } from "react";
import listaGifs from './assets/data/data.js'
import Header from "./components/Header/Header.jsx"
import Hero from "./components/Hero/Hero.jsx"
import Grid from "./components/Grid/Grid.jsx"

import './App.css'

function App() {
  //Dark Mode
  const [theme, setTheme] = useState("light");

  const handleClick = () => {
    setTheme(theme === "light" ? "dark" : "light");
  }
  //Búsqueda
  const [input, setInput] = useState();
  const [gif, setGif] = useState('');

  function buscarGIF(idGif) {
    if (input === idGif) {
      listaGifs.map((gifs)=>{
        <Grid id={gifs.id} src={gifs.url} alt={gifs.id} key={gifs.key} />
      })
    }
  }

  const handleInput = (event) => {
    setInput(event.targer.value)
  }

  const handleButton = () => {
    buscarGIF(setInput)
  }
  return (
    <div className={`${theme}`}>
      <div className="container">
        <Header handleClick={handleClick} theme={theme} />
        <Hero handleInput={handleInput} handleButton={handleButton} />
      </div>
    </div>
  );
}

export default App;
