import React, { useState, useEffect } from "react";
import Header from "./components/Header/Header.jsx"
import Hero from "./components/Hero/Hero.jsx"
import Grid from "./components/Grid/Grid.jsx"

import './App.css'

function App(props) {
  //Dark Mode
  const [theme, setTheme] = useState("light");

  const handleDark = () => {
    setTheme(theme === "light" ? "dark" : "light");
  }
  //API
  const API_KEY = "g4FgqpLBAv5phmEHXtcSt2FuIilGf156"
  const limit = 12
  const urlSearch = "https://api.giphy.com/v1/gifs/search"

  let [gifs, setGifs] = useState([]);
  let [isLoading, setIsLoading] = useState(false);
  let [search, setSearch] = useState("");

  const handleSearchChange = (e) => {
    setSearch(e.target.value)
  }
  const handleSubmit = (e) => {
    e.preventDefault();

  }
  useEffect(() => {
    setIsLoading(true);
    let peticion = fetch(
        `${urlSearch}?api_key=${API_KEY}&q=${search}&limit=${limit}`
    );
    peticion
        .then((respuesta) => {
            return respuesta.json();
        })
        .then((data) => {
            console.log(data.data)
            setGifs(data.data);
            setIsLoading(false)
        })
}, []);

  return (
    <div className={`${theme}`}>
      <div className="container">
        <Header handleDark={handleDark} theme={theme === "light" ? "dark" : "light"} />
        <Hero handleSearchChange={handleSearchChange} search={search} handleSubmit={handleSubmit} />
        <Grid/>
        <div className="resultados">
                {isLoading ? <div>Loading</div> : gifs.map((gif) => {
                    return (
                        <div key={gif.id} className="gif-result">
                            <img src={gif.images.fixed_height.url} alt={gif.title} />
                        </div>
                    )
                })}
            </div>
      </div>
    </div>
  );
}

export default App;
