import React, { useState } from "react";
import { listaGifs } from './assets/data/data.js'
import Header from "./components/Header/Header.jsx"
import Hero from "./components/Hero/Hero.jsx"
import Grid from "./components/Grid/Grid.jsx"

import './App.css'

function App(props) {
  //Dark Mode
  const [theme, setTheme] = useState("light");

  const handleClick = () => {
    setTheme(theme === "light" ? "dark" : "light");
  }
  //Búsqueda
  const [input, setInput] = useState("");
  const [filter, setFilter] = useState(false);


  const handleInput = (event) => {
    setInput(event.target.value)
    setFilter(false)
  }

  const handleButton = (event) => {
    setFilter(!filter);
    if (input === "" && setFilter(true)) {
      setFilter(false)
    } else {
      setFilter(true)
    }
  }
  const filterGif = listaGifs.filter((gif) => {
    if (filter === true && input !== "") {
      return gif.id == input
    }
    return gif
  });

  let filteredGifs = filterGif;
  const showGifs = filteredGifs.map((gifs) => {
    return(
      <Grid key={gifs.key} id={gifs.id} src={gifs.url} alt={gifs.id} />
    )
  })


  return (
    <div className={`${theme}`}>
      <div className="container">
        <Header handleClick={handleClick} theme={theme} />
        <Hero handleInput={handleInput} input={input} handleButton={handleButton} filter={filter} />
      </div>
      <div className="container">
        {showGifs}
      </div>
    </div>
  );
}

export default App;
