import React, { useState } from "react";
import listaGifs from './assets/data/data.js'
import Header from "./components/Header/Header.jsx"
import Hero from "./components/Hero/Hero.jsx"
import Grid from "./components/Grid/Grid.jsx"

import './App.css'

function App() {
  //Dark Mode
  const [theme, setTheme] = useState("light");

  const handleClick = () => {
    setTheme(theme === "light" ? "dark" : "light");
  }
  //Búsqueda
  const [input, setInput] = useState("");
  const [filter, setFilter] = useState(false);


  const handleInput = (event) => {
    setInput(event.targer.value)
  }

  const handleButton = () => {
    setFilter(!filter);
   if(input === "" && setFilter(true)){
     setFilter(false)
   } else{
     setFilter(true)
   }
  }
  const filterGif = listaGifs.filter((gif)=>{
    if (filter === true && input !== "") {
      return gif.id === input
    }
    return gif
  });

  return (
    <div className={`${theme}`}>
      <div className="container">
        <Header handleClick={handleClick} theme={theme} />
        <Hero handleInput={handleInput} input={input} handleButton={handleButton} filter={filter} />
        {
          filterGif.map((gif)=>{
            return(
              <Grid id={gif.id} src={gif.url} alt={gif.id} key={gif.key} />
            )
          })
        }
      </div>
    </div>
  );
}

export default App;
