import React, { useState, useEffect } from "react";
import Header from "./components/Header/Header.jsx"
import Hero from "./components/Hero/Hero.jsx"
import Grid from "./components/Grid/Grid.jsx"

import './App.css'

function App(props) {
  //DARK MODE
  const [theme, setTheme] = useState("light");

  const handleDark = () => {
    setTheme(theme === "light" ? "dark" : "light");
  }
  //API SEARCH
  const API_KEY = "g4FgqpLBAv5phmEHXtcSt2FuIilGf156"
  const limit = 12
  const urlSearch = "https://api.giphy.com/v1/gifs/search"

  //API AUTOCOMPLETE
  const urlAutocomplete = "https://api.giphy.com/v1/gifs/search/tags"

  let [gifs, setGifs] = useState([]);
  let [isLoading, setIsLoading] = useState(false);
  let [search, setSearch] = useState("");
  let [sugestions, setSugestions] = useState([]);

  //MANEJAR API AUTOCOMPLETE
  useEffect(() => {
    let autocomplete = fetch(`${urlAutocomplete}?api_key=${API_KEY}&q=${search}`);
    autocomplete.then((res) => { return res.json() })
      .then((dato) => {
        console.log("Autocomplete", dato.data);
        setSugestions(dato.data);
      })
  }, [search]);

  //MANEJAR INPUT DE BÚSQUEDA
  const handleSearchChange = (e) => {
    setSearch(e.target.value)
  }

  //MANEJAR BOTON DE BÚSQUEDA
  const handleSubmit = () => {
    setIsLoading(true);
    let peticion = fetch(
      `${urlSearch}?api_key=${API_KEY}&q=${search}&limit=${limit}`
    );
    peticion
      .then((respuesta) => {
        return respuesta.json();
      })
      .then((data) => {
        console.log(data.data)
        setGifs(data.data);
        setIsLoading(false)
      })

  }

  //MANEJAR BÚSQUEDA AL PRESIONAR ENTER
  const handleKey = (e) => {
    if (e.key === 'Enter') {
      setIsLoading(true);
      let peticion = fetch(
        `${urlSearch}?api_key=${API_KEY}&q=${search}&limit=${limit}`
      );
      peticion
        .then((respuesta) => {
          return respuesta.json();
        })
        .then((data) => {
          console.log(data.data)
          setGifs(data.data);
          setIsLoading(false)
        })
    }
  }

  //MANEJAR SUGERENCIAS
  const handleSugestions = (e) => {
    setSearch(e.target.getAttribute("value"));
    setIsLoading(true);
      let peticion = fetch(
        `${urlSearch}?api_key=${API_KEY}&q=${search}&limit=${limit}`
      );
      peticion
        .then((respuesta) => {
          return respuesta.json();
        })
        .then((data) => {
          console.log(data.data)
          setGifs(data.data);
          setIsLoading(false)
        })
  }
  return (
    <div className={`${theme}`}>
      <div className="container">
        <Header handleDark={handleDark} theme={theme === "light" ? "dark" : "light"} />
        <Hero handleSearchChange={handleSearchChange} search={search} handleSubmit={handleSubmit} handleKey={handleKey} sugestions={sugestions} handleSugestions={handleSugestions} />
        <Grid />
        <div className="resultados">
          {isLoading ? <div>Loading</div> : gifs.map((gif) => {
            return (
              <div key={gif.id} className="gif-result">
                <img src={gif.images.fixed_height.url} alt={gif.title} />
              </div>
            )
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
