import React, { useState } from "react";
import Header from "./components/Header/Header.jsx"
import Hero from "./components/Hero/Hero.jsx"
import Grid from "./components/Grid/Grid.jsx"

import './App.css'

function App(props) {
  //Dark Mode
  const [theme, setTheme] = useState("light");

  const handleDark = () => {
    setTheme(theme === "light" ? "dark" : "light");
  }

  //API
  const API_KEY = "g4FgqpLBAv5phmEHXtcSt2FuIilGf156"
  const limit = 12
  const urlSearch = "https://api.giphy.com/v1/gifs/search"

  //Manejar Input de búsqueda
  const [query, setQuery] = useState([]);
  const [showQuery, setShowQuery] = useState(null);

  function pedirGif(gif) {
    fetch(`${urlSearch}?api_key=${API_KEY}&q=${gif}&limit=${limit}`).then((response) => {
      return response.json()}).then((JSONresponse) => {
      console.log(JSONresponse.data);
      setShowQuery(JSONresponse.data);
    });
  }
  useEffect(() => {
    let peticion = fetch(
      `${urlSearch}?api_key=${API_KEY}&q=hola&limit=${limit}`
    );
    peticion
      .then((respuesta) => {
        return respuesta.json();
      })
      .then((data) => {
        setUsers(data.data);
      });
  }, []);

  const handleInput = (e) => {
    setQuery(e.target.value)
  }
  //Manejar botón de buscar
  const handleButton = () => {
    pedirGif(query);
  }

  return (
    <div className={`${theme}`}>
      <div className="container">
        <Header handleDark={handleDark} theme={theme === "light" ? "dark" : "light"} />
        <Hero handleInput={handleInput} showQuery={showQuery} handleButton={handleButton} />
        <Grid/>
      </div>
    </div>
  );
}

export default App;
